.lux-chat-window::before {
  background: linear-gradient(90deg, transparent 0%, color-mix(in oklab, var(--smh-accent-gold) 45%, transparent) 50%, transparent 100%);
}

.lux-chat-send-button:hover {
  box-shadow: 0 16px 28px color-mix(in oklab, var(--smh-ink) 18%, transparent), 0 0 0 1px color-mix(in oklab, var(--smh-accent-gold) 38%, transparent);
}
